import styled from "styled-components";

export const StyledCommonInput = styled.div`
    
  input {
    padding: 5px;
    border: 1px solid #8B8B8B;
    border-radius : 4px;

    ::placeholder {
        opacity : 0.9;
    }
  }
`;
