import styled from "styled-components";

export const StyledContent = styled.div`
  width: 21, 8vh;
  margin: 10px;
  border: 1px solid #a5a4a7;
  border-radius: 3px;
  background-color: #d3d2d6;

  .grid2x2 {
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
  }
  .grid2x2 > div {
    display: flex;
    flex-direction: column;
  }
  .grid2x2 > div > div {
    box-sizing: border-box;
    display: flex;
    justify-content: space-between;
    flex-direction: row;
  }

  .grid2x2 > div > div.toptext {
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
  }

  input {
    box-sizing: border-box;
    width: 80px;
  }
  /* 
  .contentFeatures {
    display: flex;
    flex-direction: row;
    box-sizing: border-box;
  }
  .a {
    display: flex;
    width: 40%;
  } */
`;
