import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";

function Content({ errors, register, index, sub_index }) {
  return (
    <StyledForm>
      <div>
        <select
          type="text"
          {...register(`products.[${index}].contents.ebat`)}
          placeholder="Alıcı adı..."
        >
          <option value="ebat 1">ebat 1</option>
          <option value="ebat 2">ebat 2</option>
          <option value="ebat 3">ebat 3</option>
        </select>
      </div>
      <div>
        {" "}
        <select
          type="text"
          {...register(`products.[${index}].contents.adet`)}
        >
          <option value="adet 1">adet 1</option>
          <option value="adet 2">adet 2</option>
          <option value="adet 3">adet 3</option>
        </select>
      </div>
      </StyledForm>
  );
}
export default Content;
