import styled from "styled-components";

export const StyledProduct = styled.div`

  display: flex;
  flex-direction: column;
  padding: 10px;
  
  /* .multiselect {
    display: block;
  }
  .multiselect > div {
    display: block;
  } */
  /* .multiselect > ul {
    display: block;;
  } */
  
  /* div:not(.multiselect-container .search-wrapper .searchWrapper .searchBox .optionContainer .optionListContainer .displayNone .multiSelectContainer) {

    background-color: #D2D3D1;
    border: 1px solid black;
    border-radius: 5px;
  } */
 div {

    background-color: #D2D3D1;
    border: 1px solid black;
    border-radius: 5px;
  }
  div > div  {
    
    border: none;
    width: 20vh; 
    padding: 10px;
  }

  div > div > div > div {
    padding: 0;
    display: flex;
    flex-direction: column;
  }

  p {
    margin: 0 auto;
    font-size: 13px;
    
  }
  button {
    width: 90px;
    height: 40px;
    
  }

`;
