import styled from "styled-components";

export const StyledProduct = styled.form`
  display: flex;
  flex-direction: column;

  div {
    background-color: #D2D3D1;
    margin: 5px 50px;
    border: 1px solid black;
    border-radius: 3px;
    justify-content: space-evenly;
    
  }
  div > div {
    border: none;
  }
  p {
    margin: 0 auto;
    font-size: 13px;
  }
  button {
    width: 150px;

  }
`;
