    //             {` ${item.pricing.currencyUnit}`}
    //           </div>
    //           <div>{fields[index + 1]?.pricing.currencyUnit && `+`}</div>
    //         </div>
    //       )
    //   )}
    // </StyledCostFooter>
    
    <StyledCostFooter>
      <div>TOPLAM TUTAR:</div>
      
      {fields.map(
        (item, index) =>
          item.pricing.currencyUnit && (
            <div key={index}>
              <div>
                {item.pricing.totalCost}
                {` ${item.pricing.currencyUnit}`}
              </div>
              <div>{fields[index + 1]?.pricing.currencyUnit && `+`}</div>
            </div>
          )
      )}
    </StyledCostFooter>
  );
  }
export default CostFooter;
