import styled from "styled-components";

export const StyledProduct = styled.div`

  display: flex;
  flex-direction: column;
  padding: 10px;
  margin: 5px;

  .mainDiv {
    display: flex;
    background-color: #E4E4E7;
    border: 1px solid black;
    border-radius: 5px;
     
  }
  .inputDiv  {

    display: flex;
    flex-direction: column;  
    border: none;
    width: 20vh; 
    padding: 10px;
  }

  .selectedItem {
    display: flex;
    flex-direction: column;  
    
    
  }
  p {
    margin: 0 auto;
    font-size: 13px;
    
  }
  button {
    width: 90px;
    height: 40px;
    
  }
  
