import React from "react";
import { StyledCostFooter } from "./styles/CostFooter.styled";

function CostFooter({ watchFields, fields }) {

  // const totalTL = fields.map(item => item.pricing.currencyUnit === "TL" ? item.reduce((acc, cur) => acc + cur.totalCost,0):0)
  // console.log(totalTL)
  
  // console.log(fields.reduce((acc,item) => acc + item.pricing.cost,0))
  //  console.log(fields.map(item => (item.pricing.totalCost!==0 )))
  // console.log(fields.reduce((fullTotalCost, currentValue) => fullTotalCost + currentValue.pricing.totalCost,0))
  return (
    <StyledCostFooter>
      <div>TOPLAM TUTAR:</div>
      {/* <div>{fields.map((acc,cur) => {return acc.concat(cur.pricing.totalCost," ",cur.pricing.currencyUnit," + ")},[])}</div> */}
      {fields.map(
        (item, index) =>
          fields.map(item => item.pricing.currencyUnit === item.pricing.currencyUnit) ? (
            <div key={index}>
              <div>
                {fields[index + 1]?.pricing.totalCost + fields[index]?.pricing.totalCost }
                {` ${item.pricing.currencyUnit}`}
              </div>
              <div>{fields[index + 1]?.pricing.currencyUnit && `+`}</div>
            </div>
          ):(
            <div key={index}>
              <div>
                {item.pricing.totalCost }
                {` ${item.pricing.currencyUnit}`}
              </div>
              <div>{fields[index + 1]?.pricing.currencyUnit && `+`}</div>
            </div>
          )
      )}
    </StyledCostFooter>
  );
}
export default CostFooter;
