import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";

const schema = yup.object().shape({});

function Selections() {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const [contentNames] = useState([
    "NEVRESİM",
    "ÇARŞAF",
    "STD. YASTIK KILIFI",
    "OXF. YASTIK KILIFI",
    "KIRLENT KILIFI",
  ]);

  const brandDataOptions = [
    { value: "Marka Bir", label: "Marka Bir" },
    { value: "Marka İki", label: "Marka İki" },
    { value: "Marka Üç", label: "Marka Üç" },
  ];

  const sizeDataOptions = [
    { value: "Beden Bir", label: "Beden Bir" },
    { value: "Beden İki", label: "Beden İki" },
    { value: "Beden Üç", label: "Beden Üç" },
  ];

  const productTypeDataOptions = [
    { value: "Ürün Cinsi Bir", label: "Ürün Cinsi Bir" },
    { value: "Ürün Cinsi İki", label: "Ürün Cinsi İki" },
    { value: "Ürün Cinsi Üç", label: "Ürün Cinsi Üç" },
  ];

  const packageTypeDataOptions = [
    { value: "Ambalaj Bir", label: "Ambalaj Bir" },
    { value: "Ambalaj İki", label: "Ambalaj İki" },
    { value: "Ambalaj Üç", label: "Ambalaj Üç" },
  ];

  const { fields, append, remove } = useFieldArray({
    control,
    name: "products",
  });

  const submitForm = (data) => {
    console.log(data);
  };
  return (
    <StyledProduct onSubmit={handleSubmit(submitForm)}>
      <button type="button" onClick={() => append({})}>
        Ürün Ekle
      </button>

      {fields.map(
        ({ id, brand, size, productType, contents }, index) => {
          return (
            <div key={id}>
              <div>
                <select
                  type="text"
                  {...register(`products.[${index}].brand`)}
                  defaultValue={brand}
                  options={brandDataOptions}
                ></select>
                
              </div>
              <div>
                {" "}
                <select
                  type="text"
                  {...register(`products.[${index}].size`)}
                  defaultValue={size}
                  options={sizeDataOptions}
                ></select>

              </div>
              <div>
                <select
                  type="text"
                  {...register(`products.[${index}].productType`, {
                    required: "Ürün Cinsi Seçiniz",
                  })}
                  options={productTypeDataOptions}
                ></select>
              </div>

              <div>
                <select
                  type="text"
                  {...register(`products.[${index}].contents`, {
                    required: "Ürün Cinsi Seçiniz",
                  })}
                  defaultValue={contents}
                  options={packageTypeDataOptions}
                ></select>
              </div>
              <div>
                <select
                  type="text"
                  {...register(`products.[${index}].ürünCinsi`, {required: 'Ürün Cinsi Seçiniz'})}
                  id='ürünCinsi'
                >
                  <option value="Ürün Cinsi 1">Ürün Cinsi 1</option>
                  <option value="Ürün Cinsi 2">Ürün Cinsi 2</option>
                  <option value="Ürün Cinsi 3">Ürün Cinsi 3</option>
                </select>
              </div>

              <button type="button" onClick={() => remove(index)}>
                Remove
              </button>
            </div>
          );
        }
      )}
      <input type="submit" />
    </StyledProduct>
  );
}

export default Selections;
