import React, { useState } from "react";
import "./styles.css";

export default function App() {
  const [receiver, setReceiver] = useState("");
  const [carikod, setCarikod] = useState("");
  const [teslim, setTeslim] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
  };
  const handleReceiver = (e) => {
    setReceiver(e.target.value);

  };
  const handlecarikod = (e) => {
    setCarikod(e.target.value);

  };
  const handleTeslim = (e) => {
    setTeslim(e.target.value);

  };
  return (
    <div className="ekleme_formu">
      <label>
        Alıcı Adı
        <input type="text" onChange={handleReceiver} value={receiver} />
      </label>
      <label>
        Cari Kod
        <input type="text" onChange={handlecarikod} value={carikod} />
      </label>
      <form>
      <label>
        Teslim
        <select name="teslimat" onChange={handleTeslim} value={teslim}>
          <option value="16.8">Teslim 1</option>
          <option value="16.7">Teslim 2</option>
          <option value="16.6">Teslim 3</option>
        </select>
      </label>
    </form>

      <button >Ekle</button>
    </div>
  );
}
