import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import { StyledContent } from "./styles/Content.styled";

function Content({
  selectedItem,
  index,
  sub_index,
  register,
  errors,
  control,
}) {
  return (
    <StyledContent>
      <div >
        <input 
          type="text"
          readOnly
          {...register(`products[${index}].content[${sub_index}].contentName`)}
          value={selectedItem.contentName}
        />
      </div>
      <div className="inputDiv">
        <select
          type="text"
          {...register(`products[${index}].content[${sub_index}].ebat`)}
          placeholder="Alıcı adı..."
        >
          <option value="ebat 1">ebat 1</option>
          <option value="ebat 2">ebat 2</option>
          <option value="ebat 3">ebat 3</option>
        </select>
      </div>
      <div className="inputDiv">
        {" "}
        <select
          type="text"
          {...register(`products[${index}].content[${sub_index}].adet`)}
        >
          <option value="adet 1">adet 1</option>
          <option value="adet 2">adet 2</option>
          <option value="adet 3">adet 3</option>
        </select>
      </div>
    </StyledContent>
  );
}
export default Content;
