import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import Content from "./Content";
import { StyledSelection } from "./styles/Selections.styled";

const schema = yup.object().shape({});

function Selections({ register, index, errors, append }) {

  const [contentNames, setContentNames] = useState([
    "Style 1",
    "Style 2",
    "Style 3",
    "Style 4",
    "Style 5",
  ]);

  // contentNames.map((e) => ({}))

  const brandDataOptions = [
    { value: "Marka Bir", label: "Marka Bir" },
    { value: "Marka İki", label: "Marka İki" },
    { value: "Marka Üç", label: "Marka Üç" },
  ];

  const sizeDataOptions = [
    { value: "Beden Bir", label: "Beden Bir" },
    { value: "Beden İki", label: "Beden İki" },
    { value: "Beden Üç", label: "Beden Üç" },
  ];

  const productTypeDataOptions = [
    { value: "Ürün Cinsi Bir", label: "Ürün Cinsi Bir" },
    { value: "Ürün Cinsi İki", label: "Ürün Cinsi İki" },
    { value: "Ürün Cinsi Üç", label: "Ürün Cinsi Üç" },
  ];

  const packageTypeDataOptions = [
    { value: "Ambalaj Bir", label: "Ambalaj Bir" },
    { value: "Ambalaj İki", label: "Ambalaj İki" },
    { value: "Ambalaj Üç", label: "Ambalaj Üç" },
  ];

  return (
    <StyledSelection>
      <div className="inputDiv">
        <select
          type="text"
          {...register(`products.[${index}].brand`)}
          placeholder="Alıcı adı..."
        >
          <option value="Marka 1">Marka 1</option>
          <option value="Marka 2">Marka 2</option>
          <option value="Marka 3">Marka 3</option>
        </select>
        <p>
          {errors.receiverName?.type === "required" && "Marka seçimi gerekli!"}
        </p>
      </div>
      <div className="inputDiv">
        {" "}
        <select type="text" {...register(`products.[${index}].size`)}>
          <option value="Beden 1">Beden 1</option>
          <option value="Beden 2">Beden 2</option>
          <option value="Beden 3">Beden 3</option>
        </select>
        <p>{errors.deliveryCode && "Beden seçimi gerekli!"}</p>
      </div>
      <div className="inputDiv">
        <select type="text" {...register(`products.[${index}].productType`)}>
          <option value="Ürün Cinsi 1">Ürün Cinsi 1</option>
          <option value="Ürün Cinsi 2">Ürün Cinsi 2</option>
          <option value="Ürün Cinsi 3">Ürün Cinsi 3</option>
        </select>
      </div>
      <div className="inputDiv">
        <Multiselect
          options={contentNames}
          onKeyPressFn={function noRefCheck() {}}
          onSearch={function noRefCheck() {}}
          isObject={false}
          onRemove={(e) => {}}
          onSelect={(e) => {   
             
            
          }}
          placeholder="İçerik Seç"
          avoidHighlightFirstOption={true}
          showCheckbox
          displayValue="value"
          style={{
            background: "white",
            searchBox: {
              border: "1px solid #374151",
              background: "white",
              color: "white",
              padding: "5px",
            },
          }}
        />
        {contentNames}
      </div>
      

      <div className="inputDiv">
        <select type="text" {...register(`products.[${index}].packageType`)}>
          <option value="Ambalaj 1">Ambalaj 1</option>
          <option value="Ambalaj 2">Ambalaj 2</option>
          <option value="Ambalaj 3">Ambalaj 3</option>
        </select>
      </div>
    </StyledSelection>
  );
}

export default Selections;
