import React, { useState, useEffect } from "react";
import {
  Controller,
  set,
  useFieldArray,
  useForm,
  useWatch,
} from "react-hook-form";
import CustomDropdown from "./CustomDropdown";
import { StyledPricing } from "./styles/Pricing.styled";

function Pricing({ register, index, errors, append, remove, control, fields }) {
  const dimensionDataOptions = [
    { value: "Ölçü Bir", label: "Ölçü Bir" },
    { value: "Ölçü İki", label: "Ölçü İki" },
    { value: "Ölçü Üç", label: "Ölçü Üç" },
  ];

  const exchangeRateDataOptions = [
    { value: "TL", label: "TL" },
    { value: "DOLAR", label: "DOLAR" },
    { value: "EURO", label: "EURO" },
  ];


  const registerName = `products[${index}].pricing`;

  return (
    <StyledPricing>
      <div className="header">
        <div>TOPLAM ADET</div>
        <div>BİRİM (ÖLÇÜ)</div>
        <div>FİYAT</div>
        <div>BİRİM (PARA)</div>
        <div>TOPLAM</div>
      </div>
      <div className="grid5x2">
        
        <div >
          <CustomDropdown
            registerName={`${registerName}.brand`}
            control={control}
            options={brandDataOptions}
            placeholder={"Marka Seçiniz..."}
            errors={errors}
            errorMessage="Marka Seçiniz!"
          />
        </div>
        {/* <p>{errors.products?.[index]?.brand?.message}</p> */}

        <div >
          <CustomDropdown
            registerName={`${registerName}.size`}
            control={control}
            options={sizeDataOptions}
            placeholder={"Beden Seçiniz..."}
            errors={errors}
            errorMessage="Beden Seçiniz!"
          />
        </div>
        <div >
          <CustomDropdown
            registerName={`${registerName}.productType`}
            control={control}
            options={productTypeDataOptions}
            placeholder={"Ürün Cinsi Seçiniz..."}
            errors={errors}
          />
        </div>

        <div>
          <CustomDropdown
            registerName={`${registerName}.packageType`}
            control={control}
            options={packageTypeDataOptions}
            placeholder={"Ürün Cinsi Seçiniz..."}
            errors={errors}
          />
        </div>
      </div>
    </StyledPricing>
  );
}

export default Pricing;
