import React from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";



const schema = yup.object().shape({
    // name: yup.string().required(),
    // amount: yup.number().required()
});

function Form() {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "products",
  });



  const submitForm = (data) => {
    console.log(data);
  };
  return (
    <StyledProduct onSubmit={handleSubmit(submitForm)}>
      
      <button type="button" onClick={() => append({})}>
        Ürün Ekle
      </button>

      {fields.map(({ id, marka, beden, ürünCinsi, İçerikler, Ambalaj }, index) => {
        return (
          <div key={id}>

            <div>
              <select type="text"
              {...register(`products.[${index}].marka`)}
              placeholder="Alıcı adı..."
              defaultValue={marka}
              >
              <option value="Teslim 1">Marka 1</option>
              <option value="Teslim 2">Marka 2</option>
              <option value="Teslim 3">Marka 3</option>
              </select>
            <p>
              {errors.receiverName?.type === "required" && "Alıcı adı gerekli!"}
            </p>
            </div>
            <div>
              {" "}
              
                <input
                  {...register(`products.[${index}].beden`)}
                  placeholder="sayı"
                  defaultValue={beden}
                />
                <p>{errors.deliveryCode && "Teslim için Özel Kod gerekli!"}</p>
              
            </div>

            <button type="button" onClick={() => remove(index)}>
              Remove
            </button>
          </div>
        );
      })}
      <input type="submit" />
    </StyledProduct>
  );
}

export default Form;
