import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import { StyledContent } from "./styles/Content.styled";
import CustomDropdown from "./CustomDropdown";
import CustomInput from "./CustomInput";

function Content({
  selectedItem,
  index,
  sub_index,
  register,
  errors,
  control,
}) {
  const registerName = `products[${index}].content[${sub_index}]`;
  return (
    <StyledContent>
      <div className="inputDiv">
        <input type="text" readOnly value={selectedItem.contentName} />
      </div>

      <div>
        <div>
          <CustomInput
            registerName={`${registerName}.height`}
            control={control}
            errors={errors}
            placeholder=""
            errorMessage="!"
            type={"number"}
            defaultValue={0}
          />
        </div>
        <div>
        <CustomInput
            registerName={`${registerName}.width`}
            control={control}
            errors={errors}
            placeholder=""
            errorMessage="!"
            type={"number"}
            defaultValue={0}
          />
        </div>
        <CustomDropdown
        registerName={`${registerName}.stitchType`}
        control={control}
        options={brandDataOptions}
        placeholder={"Seçiniz..."}
        errors={errors}
        errorMessage="Marka Seçiniz!"
             
      />
        <div>
          {" "}
          <select
            type="text"
            {...register(`products[${index}].content[${sub_index}].adet`)}
          >
            <option value="adet 1">adet 1</option>
            <option value="adet 2">adet 2</option>
            <option value="adet 3">adet 3</option>
          </select>
        </div>
      </div>
    </StyledContent>
  );
}
export default Content;
