import styled from "styled-components";

export const StyledPricing = styled.div`
margin: 10px;
  border: 1px solid #a5a4a7;
  border-radius: 3px;
  background-color: #d3d2d6;
  padding: 2px;

  .grid5x2 {
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
  }
  .grid2x2 > div {
    display: flex;
    flex-direction: column;
    padding: 2px;
  }
  .grid2x2 > div > div {
    box-sizing: border-box;
    display: flex;
    justify-content: space-between;
    padding: 2px;
    flex-direction: row;
  }
  
`;
