import React from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import Content from "./Content";
import Pricing from "./Pricing";
import Selections from "./Selections";
import { StyledProduct } from "./styles/Product.styled";
import Textures from "./Textures";

function Product({ control, index, register, submitCount, errors, fullFormField}) {
  const { fields, append, remove } = useFieldArray({
    control,
    name: `products[${index}].content`,
  });
  console.log(fullFormField.textures.quantity)
  return (
    <>
      <Selections
        register={register}
        control={control}
        errors={errors}
        append={append}
        fields={fields}
        index={index}
        remove={remove}
      />

      <div className="content-div">
        <div className="header">ÜRÜN İÇERİĞİ</div>
        {fields.map((field, sub_index) => {
          return (
            <Content
              selectedItem={field}
              control={control}
              index={index}
              sub_index={sub_index}
              register={register}
              errors={errors}
              key={field.id}
            />
          );
        })}
        
      </div>
      <Textures
          register={register}
          control={control}
          errors={errors}
          append={append}
          fields={fields}
          index={index}
          remove={remove}
      
        />
        <Pricing
          fullFormField={fullFormField}
          register={register}
          control={control}
          errors={errors}
          append={append}
          fields={fields}
          index={index}
          remove={remove}
      
        />
    </>
  );
}

export default Product;
