import React, { useState } from "react";
import "./styles.css";

export default function App() {
  const [state, setState] = React.useState({
    receiver: "",
    carikod: "",

  })

  const handleSubmit = (e) => {
    e.preventDefault();
  };
  function handleChange(evt) {
    const value =
      evt.target.type === "checkbox" ? evt.target.checked : evt.target.value;
    setState({
      ...state,
      state : [evt.target.value.receiver] 
    });
  }

  return (
    <div className="ekleme_formu">
      <label>
        Alıcı Adı
        <input type="text" onChange={handleChange} value={state.receiver} />
      </label>
      <label>
        Cari Kod
        <input type="text" onChange={handleChange} value={state.carikod} />
      </label>
      <form>
      <label>
        Teslim
        <select name="teslimat" onChange={handleChange} value={state.teslimat}>
          <option value="16.8">Teslim 1</option>
          <option value="16.7">Teslim 2</option>
          <option value="16.6">Teslim 3</option>
        </select>
      </label>
    </form>

      <button >Ekle</button>
    </div>
  );
}
