import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import Content from "./Content";
import CustomerForm from "./CustomerForm";
import Selections from "./Selections";
import { StyledForm } from "./styles/Form.styled";
import { StyledSelection } from "./styles/Selections.styled";
import { StyledContent } from "./styles/Content.styled";

function ContentForm({ index }) {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({});

  const { fields, append, remove } = useFieldArray({
    control,
    name: `products[${index}].contents`,
  });

  return (
    <>
  
      {fields.map(({ sub_id }, sub_index) => {
        return (
          <div key={sub_id}>
            <div>
              <input
                type="text"
                readOnly
                {...register(
                  `products[${index}].content[${sub_index}].contentName`
                )}
                value={selectedItem.contentName}
              />
            </div>

            <div className="inputDiv">
              <select
                type="text"
                {...register(`products.[${index}].contents.ebat`)}
                placeholder="Alıcı adı..."
              >
                <option value="ebat 1">ebat 1</option>
                <option value="ebat 2">ebat 2</option>
                <option value="ebat 3">ebat 3</option>
              </select>
            </div>
            <div className="inputDiv">
              {" "}
              <select
                type="text"
                {...register(`products.[${index}].contents.adet`)}
              >
                <option value="adet 1">adet 1</option>
                <option value="adet 2">adet 2</option>
                <option value="adet 3">adet 3</option>
              </select>
            </div>
          </div>
        );
      })}
    </>
  );
}

export default ContentForm;
