import React from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";

const schema = yup.object().shape({
  // name: yup.string().required(),
  // amount: yup.number().required()
});

function Form() {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "products",
  });

  const submitForm = (data) => {
    console.log(data);
  };
  return (
    <StyledProduct onSubmit={handleSubmit(submitForm)}>
      <button type="button" onClick={() => append({})}>
        Ürün Ekle
      </button>

      {fields.map(
        ({ id, marka, beden, ürünCinsi, İçerikler, Ambalaj }, index) => {
          return (
            <div key={id}>
              <div>
                <select
                  type="text"
                  {...register(`products.[${index}].marka`)}
                  placeholder="Alıcı adı..."
                  defaultValue={marka}
                >
                  <option value="Marka 1">Marka 1</option>
                  <option value="Marka 2">Marka 2</option>
                  <option value="Marka 3">Marka 3</option>
                </select>
                <p>
                  {errors.receiverName?.type === "required" &&
                    "Alıcı adı gerekli!"}
                </p>
              </div>
              <div>
                {" "}
                <select
                  type="text"
                  {...register(`products.[${index}].beden`)}
                  placeholder="Seçiniz..."
                  defaultValue={beden}
                >
                  <option value="Beden 1">Beden 1</option>
                  <option value="Beden 2">Beden 2</option>
                  <option value="Beden 3">Beden 3</option>
                </select>
                <p>{errors.deliveryCode && "Teslim için Özel Kod gerekli!"}</p>
              </div>
              <div>
              <select
                  type="text"
                  {...register(`products.[${index}].ürünCinsi`)}
                  placeholder="Alıcı adı..."
                  defaultValue={"Seçiniz"}
                >
                  <option disabled={true} value="">Seçiniz</option>
                  <option value="Ürün Cinsi 1">Ürün Cinsi 1</option>
                  <option value="Ürün Cinsi 2">Ürün Cinsi 2</option>
                  <option value="Ürün Cinsi 3">Ürün Cinsi 3</option>
                </select>
              </div>

              <button type="button" onClick={() => remove(index)}>
                Remove
              </button>
            </div>
          );
        }
      )}
      <input type="submit" />
    </StyledProduct>
  );
}

export default Form;
