import React from "react";
import { useFieldArray } from "react-hook-form";
import CustomDropdown from "./CustomDropdown";

function Textures({
  register,
  index,
  errors,
  control,
}) {
  
  const { fields, append, remove } = useFieldArray({
    control,
    name: `products[${index}].textures`,
  });

  // const registerName = `products[${index}].textures.[${sub_index}]`;
  const groupDataOptions = [
    { value: "Grup Bir", label: "Grup Bir" },
    { value: "Grup İki", label: "Grup İki" },
    { value: "Grup Üç", label: "Grup Üç" },
  ];

  return (
    <div>
        <button type="button" onClick={() => append({})}>+</button>
      {fields.map(({ id }, sub_index) => {
        return (
          <div key={id}>
            <div>
              <div className="removeButton">
                <button type="button" onClick={() => remove(sub_index)}>
                  Sil
                </button>
              </div>
            </div>

            <CustomDropdown
              registerName={`${registerName}.group`}
              control={control}
              options={groupDataOptions}
              placeholder={"Grup yok"}
              errors={errors}
              errorMessage="Grup Seçiniz!"
            />
          </div>
        );
      })}
    </div>
  );
}
export default Textures;
