import styled from "styled-components";

export const StyledForm = styled.div`
  display: flex;
  justify-content: space-evenly;
  margin: 10px;

  div {
    display: flex;
    flex-direction: column;

  }
  input {
    
    border: 1px solid grey;
    border-radius: 3px;
    
  }
  p {
    margin: 0 auto;
    font-size: 13px;

  }
`;
