import styled from "styled-components";

export const StyledTexture = styled.div`

  display: flex;
  flex-direction: column;
  align-items: center;
  padding : 5px;
  margin: 5px;
  position: absolute ;
  left : 50%;
  right : 50%;
  transform: translate(-50%);
  
 
  .grid-item > div > div {
    display: flex;
    padding: 5px;
  }
 
  .grid-item {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    
  }
  .grid-item > div {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    padding: 5px;
  }
  .numInput > input {
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    width: 4vw;
    height: 3.5vh;
    padding: 0.5vw;
  }
`;
