import styled from "styled-components";

export const StyledProduct = styled.div`

  display: flex;
  flex-direction: column;
  padding: 10px;
  

  div {
    display: flex;
    flex-direction: column;
    background-color: #D2D3D1;
    border: 1px solid black;
    border-radius: 3px;
    
    
  }
  div > div {
    display: flex;
    flex-direction: row;
    border: none;
    width: 20vh; 
    padding: 10px;
  }
  div > div > div > div {
    padding: 0;
  }

  p {
    margin: 0 auto;
    font-size: 13px;
  }
  button {
    width: 150px;
    
  }

`;
