import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import { StyledContent } from "./styles/Content.styled";
import CustomDropdown from "./CustomDropdown";
import CustomInput from "./CustomInput";
import { ToggleSwtich } from "./styles/ToggleSwitch.styled";
import ToggleSwitch from "./ToggleSwitch";
import ReadySize from "./ReadySize";

function Content({
  selectedItem,
  index,
  sub_index,
  errors,
  control,
  register,
}) {

  const stitchDataOptions = [
    { value: "Dikiş Bir", label: "Dikiş Bir" },
    { value: "Dikiş İki", label: "Dikiş İki" },
    { value: "Dikiş Üç", label: "Dikiş Üç"},
  ];

  const readySizeDataOptions = [
    { value: "Ebat Bir", label: "Ebat Bir", color:"" },
    { value: "Ebat İki", label: "Ebat İki", color: ""},
    { value: "Ebat Üç", label: "Ebat Üç" , color:"" },
  ];

  const readyWeightDataOptions = [
    { value: "Gramaj Bir", label: "Gramaj Bir" },
    { value: "Gramaj İki", label: "Gramaj İki" },
    { value: "Gramaj Üç", label: "Gramaj Üç" },
  ];

  const customStyles = {
    
    control: (styles) => ({
      ...styles, backgroundColor: "white", 
      height: "25px",
      minHeight: "25px",
      fontSize: "12px",
     

    }),
    valueContainer: (state) => ({
      ...state,
      height: '25px',
      padding: '0 6px'
    }),

    input: (state) => ({
      ...state,
      margin: '0px',
    }),
    indicatorSeparator: state => ({
      display: 'none',
    }),
    indicatorsContainer: (state) => ({
      ...state,
      height: '25px',
    }),
    option: (styles, {data, isDisabled, isFocused, isSelected}) => {
      return {...styles, color: data.color};

    },
    singleValue: (styles, {data}) => {
      return { ...styles,
      backgroundColor: data.color,
      color: "#C84555" };
    }
  }

  const [isSizeToggled, setIsSizeToggled] = useState(false);
  const [isWeightToggled, setIsWeightToggled] = useState(false);

  const registerName = `products[${index}].content[${sub_index}]`;

  return (
    <StyledContent>
      <div>
        <input type="text" readOnly value={selectedItem.contentName} />
      </div>
      <div className="grid2x2">
        <div>
          {!isSizeToggled ? (
            <>
              <div>
                <div className="lefttext">BOY:</div>
                <CustomInput 
                  registerName={`${registerName}.height`}
                  control={control}
                  errors={errors}
                  placeholder={"cm"}
                  errorMessage="Boy 0 dan büyük olmalı"
                  type={"number"}
                />
              </div>
              <div>
                <div className="lefttext">EN:</div>
                <CustomInput
                  registerName={`${registerName}.width`}
                  control={control}
                  errors={errors}
                  placeholder={"cm"}
                  errorMessage="En 0 dan büyük olmalı"
                  type={"number"}
                />
              </div>
            </>
          ) : (
            <div className="leftSideInput">
              <CustomDropdown
                customStyles={customStyles}
                registerName={`${registerName}.readySize`}
                control={control}
                options={readySizeDataOptions}
                placeholder={"Seçiniz..."}
                errors={errors}
                errorMessage="Hazır Ebat Seçiniz!"
              />
            </div>
          )}

          <div>
            <p>Hazır Ebat</p>
            <ToggleSwitch
              isToggled={isSizeToggled}
              onToggle={() => setIsSizeToggled(!isSizeToggled)}
              registerName={`${registerName}.isSizeToggled`}
              register={register}
            />
          </div>

          <div className="toptext">
            <p>DİKİŞ</p>
            <div>
              <CustomDropdown
                registerName={`${registerName}.stitchType`}
                control={control}
                options={stitchDataOptions}
                placeholder={"Seçiniz..."}
                errors={errors}
                errorMessage="Dikiş Seçiniz!"
              />
            </div>
          </div>
        </div>
        <div>
          <div className="toptext">
            <p>ADET:</p>
            <div>
              <CustomInput
                registerName={`${registerName}.piece`}
                control={control}
                errors={errors}
                placeholder=""
                errorMessage="Adet giriniz!"
                type={"number"}
                defaultValue={0}
              />
            </div>
          </div>
          <div>
            <p>Gramaj</p>
            <ToggleSwitch
              isToggled={isWeightToggled}
              onToggle={() => setIsWeightToggled(!isWeightToggled)}
              registerName={`${registerName}.isWeightToggled`}
              register={register}
            />
          </div>
          <div>
            {!isWeightToggled ? (
              <CustomInput
                registerName={`${registerName}.weight`}
                control={control}
                errors={errors}
                placeholder=""
                errorMessage="Gramaj giriniz!"
                type={"number"}
                defaultValue={0}
              />
            ) : (
              <CustomDropdown
                registerName={`${registerName}.readyWeight`}
                control={control}
                options={readyWeightDataOptions}
                placeholder={"Seçiniz..."}
                errors={errors}
                errorMessage="Gramaj Seçiniz!"
              />
            )}
          </div>
        </div>
      </div>
    </StyledContent>
  );
}
export default Content;
