import styled from "styled-components";

export const StyledForm = styled.div`
  display: flex;
  justify-content: space-evenly;
  margin: 10px;

  .inputDiv {
    display: flex;
    flex-direction: column;
    border: none;
    width: 20vh;
    padding: 10px;
  }
  input {
    border: 1px solid grey;
    border-radius: 3px;
  }
  p {
    margin: 0 auto;
    font-size: 13px;
  }
`;
