import styled from "styled-components";

export const StyledProduct = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;

  .mainDiv {
    display: flex;
    background-color: #e4e4e7;
    border: 1px solid black;
    border-radius: 5px;
    position: absolute;
  }
  .inputDiv {
    display: flex;
    flex-direction: column;
    border: none;
    width: 20vh;
    padding: 10px;
  }

  .selectedItem {
    display: flex;
    flex-direction: column;
  }
  p {
    margin: 0;
    font-size: 13px;
  }
  .header {
    display: flex;
    flex-direction: column;
    box-sizing: border-box;
    border: none;
    padding: 10px;
    display: flex;
    border: none;
    padding: 10px;
    align-items: center;
  }

  .removeButton {
    display: flex;
    flex-direction: column;
    box-sizing: border-box;
    border: none;
    padding: 10px;
    display: flex;
    border: none;
    padding: 10px;
    align-items: center;
    & button {
      border: 1px solid black;
      border-radius: 5px;
      background-color: #ff5154;
      color: white;
      height: 40px;
      width: 50px;
      font-weight: 500;
      cursor: pointer;
      line-height: 16px;
      text-align: center;
      box-sizing: border-box;
      padding: 12px 14px;
      outline: 0;
      box-shadow: rgba(0, 0, 0, 0.1) 1px 2px 4px;
      :hover,
      :active {
        background-color: #ff6567;
        background-position: 0 0;
        color: white;
      }
    }
  }
  
  /* div > div > div > div {
    padding: 0;
    
  } */

  /* .multiselect {
    display: block;
  }
  .multiselect > div {
    display: block;
  } */
