    display: block;;
  } */

  /* .multiselect-container .search-wrapper .searchWrapper .searchBox .optionContainer .optionListContainer .displayNone .multiSelectContainer {
    display: "webkit-box"
    background-color: #D2D3D1;
    border: 1px solid black;
    border-radius: 5px;
  } */

  /* .displayNone{
    display: none;
  }
  .displayBlock{
    display: block;
  }
  .chip {
    display: inline-flex;
    align-items: center;
  } */
`;
