import styled from "styled-components";

export const StyledProduct = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;

  .mainDiv {
    display: flex;
    background-color: #e4e4e7;
    border: 1px solid black;
    border-radius: 5px;
  }
  .inputDiv {
    display: flex;
    flex-direction: column;
    border: none;
    width: 20vh;
    padding: 10px;
  }

  .selectedItem {
    display: flex;
    flex-direction: column;
  }
  p {
    margin: 0;
    font-size: 13px;
  }
  .removeButtonDiv {
    display: flex;
    flex-direction: column;
    border: none;
    padding: 10px;
    & button {
      border: 1px solid black;
      border-radius: 5px;
      background-color: #fa3f42;
      color: white;
      height: 40px;
      width: 50px;
      font-weight: 500;
      cursor: pointer;
      line-height: 16px;
      text-align: center;
      box-sizing: border-box;
      padding: 12px 14px;
      outline: 0;
      button:hover,
      button:active {
        background-color: initial;
        background-position: 0 0;
        color: #ff4742;
        opacity: .5;
      }
    }
  }

