import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import Content from "./Content";
import CustomerForm from "./CustomerForm";
import Selections from "./Selections";

// const schema = yup.object().shape({
//   receiverName: yup.string().required(),
//   currentCode: yup.string().required(),
//   delivery: yup.string().required(),
//   deliveryCode: yup.string().required(),
//   seller: yup.string().required(),
//   bank: yup.string().required(),
//   paymentMethod: yup.string().required(),
//   invoice: yup.number().positive().required(),
// });

function FullForm() {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({});

  const { fields, append, remove } = useFieldArray({
    control,
    name: "products",
  });

  const submitForm = (data) => {
    console.log(data);
  };
  return (
    
        
      <form onSubmit={handleSubmit(submitForm)}>
        <StyledForm>
            <CustomerForm errors={errors} register={register}/>
        </StyledForm>
        
        <StyledProduct>
        <button type="button" onClick={() => append({})}>
          Ürün Ekle
        </button>

        {fields.map(({ id }, index) => {
          return (
            <div key={id}>
              <div>
                <Selections errors={errors} register={register} index={index} />
              </div>
              <div>
                <button type="button" onClick={() => remove(index)}>
                  Remove
                </button>
              </div>
              
            </div>
          );
        })}
        
        <input type="submit" />
        </StyledProduct>
      </form>
    
  );
}

export default FullForm;
