import styled from "styled-components";

export const StyledContent = styled.div`
  width: 21, 8vh;
  margin: 10px;
  border: 1px solid #a5a4a7;
  border-radius: 3px;
  background-color: #d3d2d6;
  padding : 5px;

  .grid2x2 {
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
  }
  .grid2x2 > div {
    display: flex;
    flex-direction: column;
    padding: 2px;
    width: 150px;
    height: 100px;
  }
  .grid2x2 > div > div {
    box-sizing: border-box;
    display: flex;
    justify-content: space-between;
    padding: 2px;
    flex-direction: row;
    
  }
  .grid2x2 > div > div.toptext {
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
  }

  input {
    border: 1px solid #a5a4a7;
    width: 80px;
    border-radius: 3px;
    box-sizing: border-box;
    
  }
  p {
    display: flex;
    align-items: center;
  }
  /* 
  .contentFeatures {
    display: flex;
    flex-direction: row;
    box-sizing: border-box;
  }
  .a {
    display: flex;
    width: 40%;
  } */
`;
