import styled from "styled-components";

export const StyledProduct = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;

  .mainDiv {
    display: flex;
    background-color: #e4e4e7;
    border: 1px solid black;
    border-radius: 5px;
  }
  .inputDiv {
    display: flex;
    flex-direction: column;
    border: none;
    width: 20vh;
    padding: 10px;
  }

  .selectedItem {
    display: flex;
    flex-direction: column;
  }
  p {
    margin: 0;
    font-size: 13px;
  }
  .removeButtonDiv {
    display: flex;
    flex-direction: column;
    border: none;
    padding: 10px;
    & button {
      background: #ff4742;
      border: 1px solid #ff4742;
      border-radius: 6px;
      box-shadow: rgba(0, 0, 0, 0.1) 1px 2px 4px;
      box-sizing: border-box;
      color: #ffffff;
      cursor: pointer;
      display: inline-block;
      font-family: nunito, roboto, proxima-nova, "proxima nova", sans-serif;
      font-size: 16px;
      font-weight: 800;
      line-height: 16px;
      min-height: 40px;
      outline: 0;
      padding: 12px 14px;
      text-align: center;
      text-rendering: geometricprecision;
      text-transform: none;
      user-select: none;
      -webkit-user-select: none;
      touch-action: manipulation;
      vertical-align: middle;
    }

    button:hover,
    button:active {
      background-color: initial;
      background-position: 0 0;
      color: #ff4742;
    }

    button:active {
      opacity: 0.5;
    }
  }

  /* div > div > div > div {
    padding: 0;
    
  } */

