import styled from "styled-components";

export const StyledProduct = styled.div`

  display: flex;
  flex-direction: column;
  

  div {
    flex-direction: row;
    background-color: #D2D3D1;
    border: 1px solid black;
    border-radius: 3px;
    flex-wrap: wrap;
    
  }
  div > div {
    border: none;
    width: 20vh; 
  }
  div.ege {
    
  }

  p {
    margin: 0 auto;
    font-size: 13px;
  }
  button {
    width: 150px;
  }

`;
