import React from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";

const schema = yup.object().shape({
  // name: yup.string().required(),
  // amount: yup.number().required()
});

function Form() {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "products",
  });

  const submitForm = (data) => {
    console.log(data);
  };
  return (
    <StyledProduct onSubmit={handleSubmit(submitForm)}>
      <div>
        <button type="button" onClick={() => append({})}>
          Ürün Ekle
        </button>

        {fields.map(({ id, name, amount }, index) => {
          return (
            <div key={id}>
              <input
                type="text"
                {...register(`products.[${index}].name`)}
                placeholder="Alıcı adı..."
                defaultValue={name}
              />
              <p>
                {errors.receiverName?.type === "required" &&
                  "Alıcı adı gerekli!"}
              </p>
              <div>
                {" "}
                <div>
                  <input
                    {...register(`products.[${index}].amount`)}
                    placeholder="sayı"
                    defaultValue={amount}
                  />
                  <p>
                    {errors.deliveryCode && "Teslim için Özel Kod gerekli!"}
                  </p>
                </div>
              </div>

              <button type="button" onClick={() => remove(index)}>
                Remove
              </button>
            </div>
          );
        })}
        <input type="submit" />
      </div>
    </StyledProduct>
  );
}

export default Form;
