import React from "react";
import { useFieldArray } from "react-hook-form";
import CustomDropdown from "./CustomDropdown";
import { StyledTexture } from "./styles/Texture.styled";
import TextureItems from "./TextureItems";

function Textures({ register, index, errors, control }) {
  const { fields, append, remove } = useFieldArray({
    control,
    name: `products[${index}].textures`,
  });

  return (
    <StyledTexture>
      
      {fields.map(({ id }, sub_index) => {
        return (
          <div className="grid-item" key={id}>
            
            <div>
            <div>İŞLEM</div>
              <button className="remove-button" type="button" onClick={() => remove(sub_index)}>
                -
              </button>
            </div>
            <TextureItems
              index={index}
              control={control}
              errors={errors}
              sub_index={sub_index}
            />
          </div>
          
        );
      })}
      <div className="add-item" >
        <button type="button" onClick={() => append({})}>
          +
        </button>
      </div>
    </StyledTexture>
  );
}
export default Textures;
