import React from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import Content from "./Content";
import Selections from "./Selections";
import { StyledProduct } from "./styles/Product.styled";

function Product({
  control,
  index,
  register,
  productRemove,
  submitCount,
  errors,
}) {


  const { fields, append, remove } = useFieldArray({
    control,
    name: `products[${index}].contents`,
  });

  return (
    <>
      <Selections
        register={register}
        control={control}
        errors={errors}
        
        append={append}
        fields={fields}
      />

      <div>
        {fields.map((field, sub_index) => {
          return (
            <Content
              selectedItem={field}
              control={control}
              update={update}
              index={index}
              sub_index={sub_index}
              register={register}
              errors={errors}
              key={field.id}
            />
          );
        })}
      </div>
    </>
  );
}

export default Product;
