import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import CustomDropdown from "./CustomDropdown";
import { StyledCustomerForm } from "./styles/CustomerForm.styled";
import { StyledCommonInput } from "./styles/CommonInput.styled";
import CustomInput from "./CustomInput";
import { StyledHeader } from "./styles/InputHeader.styled";

function CustomerForm({ errors, register, control }) {
  const deliveryOptions = [
    { value: "Teslim Bir", label: "Teslim Bir" },
    { value: "Teslim İki", label: "Teslim İki" },
    { value: "Teslim Üç", label: "Teslim Üç" },
  ];
  const dealerOptions = [
    { value: "Satıcı Bir", label: "Satıcı Bir" },
    { value: "Satıcı İki", label: "Satıcı İki" },
    { value: "Satıcı Üç", label: "Satıcı Üç" },
  ];
  const bankOptions = [
    { value: "Banka Bir", label: "Banka Bir" },
    { value: "Banka İki", label: "Banka İki" },
    { value: "Banka Üç", label: "Banka Üç" },
  ];
  const paymentMethodOptions = [
    { value: "Nakit", label: "Nakit" },
    { value: "Kredi Kartı", label: "Kredi Kartı" },
    { value: "Cüzdan", label: "Cüzdan" },
  ];
  return (
    <StyledCustomerForm>
      <StyledCommonInput>
        <StyledHeader>ALICI ADI</StyledHeader>
        <CustomInput
          registerName={"receiverName"}
          control={control}
          placeholder={"Alcı adı..."}
          errors={errors}
          type="text"
        />
        
      </StyledCommonInput>

      <StyledCommonInput>
      <StyledHeader>ALICI CARİ KOD</StyledHeader>
        <CustomInput
          registerName={"currentCode"}
          control={control}
          placeholder={"Cari kod..."}
          errors={errors}
          type="text"
        />
      </StyledCommonInput>

      <StyledCommonInput>
      <StyledHeader>TESLİM</StyledHeader>
        <CustomDropdown
          registerName={`delivery`}
          control={control}
          options={deliveryOptions}
          placeholder={"Seçiniz..."}
          errors={errors}
        />
      </StyledCommonInput>

      <StyledCommonInput>
      
      <StyledHeader>TESLİM İÇİN ÖZEL KOD</StyledHeader>
        <CustomInput
          registerName={"deliveryCode"}
          control={control}
          placeholder={"Teslim için özel kod..."}
          errors={errors}
          type="text"
        />
      </StyledCommonInput>
      <StyledCommonInput>
      
        <CustomDropdown
          registerName={`dealer`}
          control={control}
          options={bankOptions}
          placeholder={"Seçiniz..."}
          errors={errors}
        />
      </StyledCommonInput>
      <StyledCommonInput>
        <CustomDropdown
          registerName={`bank`}
          control={control}
          options={dealerOptions}
          placeholder={"Seçiniz..."}
          errors={errors}
        />
      </StyledCommonInput>
      <StyledCommonInput>
        <CustomDropdown
          registerName={`paymentMethod`}
          control={control}
          options={paymentMethodOptions}
          placeholder={"Seçiniz..."}
          errors={errors}
        />
      </StyledCommonInput>
      <StyledCommonInput>
        <CustomInput
          registerName={"invoice"}
          control={control}
          placeholder={"Fatura no..."}
          errors={errors}
          type="text"
        />
      </StyledCommonInput>
    </StyledCustomerForm>
  );
}

export default CustomerForm;
