import styled from "styled-components";

export const StyledPricing = styled.div`
  .grid-container {
    grid-template-columns: auto auto auto;
    padding: 10px;
    display: grid;
  }
  

  .grid-container > div {
    padding: 20px;
    text-align: center;
  }
`;
