import styled from "styled-components";

export const StyledPricing = styled.div`
  border: 1px solid #a5a4a7;
  border-radius: 3px;
  background-color: #d3d2d6;

  .header {
    display: flex;
    flex-direction: row;
  }
  .numInput > input {
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    width: 60px;
    height: 40px;
    padding: 4px;
  }
  .header > div {
    margin-left: 30px;
    margin-right: 30px;
  }
  .grid5x2 {
    display: flex;
    flex-direction: row;
  }
  .grid5x2 > div {
    display: flex;
    flex-direction: column;
    padding: 2px;
  }
`;
