import React from "react";
import { Controller, useForm } from "react-hook-form";
import Select from "react-select";

function CustomDropdown({ registerName, control, options, placeholder, errors}) {
  return (
    <div className="inputDiv">
        
      <Controller
        name={registerName}
        required={true}
        control={control}
        render={({ field: { onChange, value, ref } }) => {
          const currentSelection = options.find((o) => o.value === value);
          const handleSelectChange = (selectedOption) => {
            onChange(selectedOption?.value);
          };
          return (
            <Select
              value={currentSelection}
              options={options}
              onChange={handleSelectChange}
              type="text"
              placeholder={placeholder}
              onClick={()=> setError('products',{ type: 'custom', message: 'something is wrong'})}
            
            />
          );
        }}
      />
      <p>{errors.products?.message}</p>
    </div>
  );
}

export default CustomDropdown;
