import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledForm } from "./styles/Form.styled";
import CustomDropdown from "./CustomDropdown";

function CustomerForm({ errors, register, control }) {

  const deliveryOptions = [
    { value: "Teslim Bir", label: "Teslim Bir" },
    { value: "Teslim İki", label: "Teslim İki" },
    { value: "Teslim Üç", label: "Teslim Üç" },
  ];
  const dealerOptions = [
    { value: "Satıcı Bir", label: "Satıcı Bir" },
    { value: "Satıcı İki", label: "Satıcı İki" },
    { value: "Satıcı Üç", label: "Satıcı Üç" },
  ];
  const bankOptions = [
    { value: "Banka Bir", label: "Banka Bir" },
    { value: "Banka İki", label: "Banka İki" },
    { value: "Banka Üç", label: "Banka Üç" },
  ];
  const paymentMethodOptions = [
    { value: "Nakit", label: "Nakit" },
    { value: "Kredi Kartı", label: "Kredi Kartı" },
    { value: "Cüzdan", label: "Cüzdan" },
  ];
  return (
    <StyledForm>
      <div>
        <input
          type="text"
          name="receiverName"
          {...register("receiverName")}
          placeholder="Alıcı adı..."
        />

      </div>

      <div>
        <input
          name="currentCode"
          {...register("currentCode")}
          placeholder="Cari kod..."
        />
      </div>
      
      <CustomDropdown
        registerName={`delivery`}
        control={control}
        options={deliveryOptions}
        placeholder={"Teslim Seçiniz..."}
      />

      <div>
        <input
          name="deliveryCode"
          {...register("deliveryCode")}
          placeholder="Teslim için Özel Kod..."
        />
        <p>{errors.deliveryCode && "Teslim için Özel Kod gerekli!"}</p>
      </div>
      <div>
        <select {...register("seller")}>
          <option value="Satıcı 1">Satıcı 1</option>
          <option value="Satıcı 2">Satıcı 2</option>
          <option value="Satıcı 3">Satıcı 3</option>
        </select>
      </div>
      <div>
        <select {...register("bank")}>
          <option value="Banka 1">Banka 1</option>
          <option value="Banka 2">Banka 2</option>
          <option value="Banka 3">Banka 3</option>
        </select>
      </div>
      <div>
        <select {...register("paymentMethod")}>
          <option value="Nakit">Nakit</option>
          <option value="Kredi Kartı">Kredi Kartı</option>
          <option value="Teslim 3">Teslim 3</option>
        </select>
      </div>
      <div>
        <input
          type="number"
          name="invoice"
          {...register("invoice")}
          placeholder="Fatura No..."
        />
      </div>
    </StyledForm>
  );
}

export default CustomerForm;
