import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const schema = yup.object().shape({
  receiverName: yup.string().required(),
  currentCode: yup.string().required(),
  delivery: yup.string().required(),
  deliveryCode: yup.string().required(),
  seller: yup.string().required(),
  bank: yup.string().required(),
  paymentMethod: yup.string().required(),
  invoice: yup.number().positive().required(),
});

function Form() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const submitForm = (data) => {
    console.log(data);
  };
  return (

      <div className="inputs">
        <form onSubmit={handleSubmit(submitForm)}>
          <input
            type="text"
            name="receiverName"
            {...register("receiverName")}
            placeholder="Alıcı adı..."
          />
          <p>
            {errors.receiverName?.type === "required" && "Alıcı adı gerekli!"}
          </p>
          <input
            name="currentCode"
            {...register("currentCode")}
            placeholder="Cari kod..."
          />
          <p>{errors.currentCode && "Cari kod gerekli!"}</p>
      
          <select {...register("delivery")}>
            <option value="Teslim 1">Teslim 1</option>
            <option value="Teslim 2">Teslim 2</option>
            <option value="Teslim 3">Teslim 3</option>
          </select>
          <input
            name="deliveryCode"
            {...register("deliveryCode")}
            placeholder="Teslim için Özel Kod..."
          />
          <p>{errors.deliveryCode && "Teslim için Özel Kod gerekli!"}</p>
          <select {...register("seller")}>
            <option value="Satıcı 1">Satıcı 1</option>
            <option value="Satıcı 2">Satıcı 2</option>
            <option value="Satıcı 3">Satıcı 3</option>
          </select>
          <select {...register("bank")}>
            <option value="Banka 1">Banka 1</option>
            <option value="Banka 2">Banka 2</option>
            <option value="Banka 3">Banka 3</option>
          </select>
          <select {...register("paymentMethod")}>
            <option value="Nakit">Nakit</option>
            <option value="Kredi Kartı">Kredi Kartı</option>
            <option value="Teslim 3">Teslim 3</option>
          </select>
          <input
            type="number"
            name="invoice"
            {...register("invoice")}
            placeholder="Fatura No..."
          />
          <input type="submit" id="submit" />
        </form>
      </div>
  );
}

export default Form;
