import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const schema = yup.object().shape({
  receiverName: yup.string().required(),
  currentCode: yup.number().positive().required(),
  delivery: yup.string().required(),
  age: yup.number().positive().integer().required(),
  password: yup.string().min(4).max(15).required(),
  confirmPassword: yup.string().oneOf([yup.ref("password"), null]),
});

function Form() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const submitForm = (data) => {
    console.log(data);
  };
  return (
    <div className="Form">
      <div className="title">Sign Up</div>
      <div className="inputs">
        <form onSubmit={handleSubmit(submitForm)}>
          <input
            type="text"
            name="receiverName"
            {...register("receiverName")}
            placeholder="Alıcı adı..."
          />
          <p>
            {errors.receiverName?.type === "required" && "Alıcı adı gerekli!"}
          </p>
          <input
            name="currentCode"
            {...register("currentCode")}
            placeholder="Cari kod..."
          />
          <p>{errors.currentCode && "Cari kod gerekli!"}</p>
      
          <select {...register("delivery")}>
            <option value="Teslim 1">Teslim 1</option>
            <option value="Teslim 2">Teslim 2</option>
            <option value="Teslim 3">Teslim 3</option>
          </select>
          <select {...register("delivery")}>
            <option value="Teslim 1">Teslim 1</option>
            <option value="Teslim 2">Teslim 2</option>
            <option value="Teslim 3">Teslim 3</option>
          </select>
          <input type="submit" id="submit" />
        </form>
      </div>
    </div>
  );
}

export default Form;
