import React, { useState } from "react";
import {
  FormProvider,
  useFieldArray,
  useForm,
  useWatch,
} from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import Content from "./Content";
import CustomerForm from "./CustomerForm";
import Selections from "./Selections";
import { StyledForm } from "./styles/Form.styled";
import { StyledSelection } from "./styles/Selections.styled";
import Product from "./Product";

// const schema = yup.object().shape({
//   receiverName: yup.string().required(),
//   currentCode: yup.string().required(),
//   delivery: yup.string().required(),
//   deliveryCode: yup.string().required(),
//   seller: yup.string().required(),
//   bank: yup.string().required(),
//   paymentMethod: yup.string().required(),
//   invoice: yup.number().positive().required(),
// });

function FullForm() {
  const {
    register,
    handleSubmit,
    setError,
    control,
    watch,
    formState: { errors },
  } = useForm({
    defaultValues: {
      products: "",
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "products",
  });

  console.log(watch())
  const submitForm = (data) => {
    console.log(data);
    // setError('products',{ type: 'custom'})
  };
  return (
    <form onSubmit={handleSubmit(submitForm)}>
      <CustomerForm errors={errors} register={register} control={control} />

      <button type="button" onClick={() => append({})}>
        Ürün Ekle
      </button>
      <input type="submit" />
      <StyledProduct>
        {fields.map((field, index) => {
          return (
            <div key={field.id} className="mainDiv">
              <div>
                <div className="header">ID</div>
                <div className="header">{index}</div>
                <div className="removeButton">
                  <button type="button" onClick={() => remove(index)}>
                    SİL
                  </button>
                </div>
              </div>

              <Product
                control={control}
                index={index}
                register={register}
                errors={errors}
              />
            </div>
          );
        })}
      </StyledProduct>
    </form>
  );
}

export default FullForm;
