import styled from "styled-components";

export const StyledContent = styled.div`
  display: flex;
  flex-direction: column;

  div {
    background-color: #d2d3d1;

    margin: 10px 10px 10px 10px;
    border: 1px solid black;
    border-radius: 3px;
  }
`;
