import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import Content from "./Content";

const schema = yup.object().shape({});

function Selections() {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const [contentNames, setContentNames] = useState([
    "KILIF 1",
    "KILIF 2",
    "KILIF 3",
    "KILIF 4",
    "KILIF 5",
  ]);


  const { fields, append, remove } = useFieldArray({
    control,
    name: "products",
  });

  const submitForm = (data) => {
    console.log(data);
  };
  return (
    <StyledProduct onSubmit={handleSubmit(submitForm)}>
      <button type="button" onClick={() => append({})}>
        Ürün Ekle
      </button>

      {fields.map(
        ({ id, brand, size, productType, contents, packageType }, index) => {
          return (
            <div key={id}>
              <Multiselect
                {...register(`products.[${index}].contents`)}
                options={contentNames}
                onKeyPressFn={function noRefCheck() {}}
                onSearch={function noRefCheck() {}}
                isObject={false}
                onRemove={(e) => {}}
                onSelect={(e) => {}}
                placeholder="İçerik Seç"
                avoidHighlightFirstOption={true}
                showCheckbox
                displayValue="value"
                style={{
                  background: "white",
                  searchBox: {
                    border: "1px solid #374151",
                    background: "white",
                    color: "white",
                  },
                }}
              />
              <div>
                <select
                  type="text"
                  {...register(`products.[${index}].brand`)}
                  placeholder="Alıcı adı..."
                >
                  <option value="Marka 1">Marka 1</option>
                  <option value="Marka 2">Marka 2</option>
                  <option value="Marka 3">Marka 3</option>
                </select>
                <p>
                  {errors.receiverName?.type === "required" &&
                    "Marka seçimi gerekli!"}
                </p>
              </div>
              <div>
                {" "}
                <select type="text" {...register(`products.[${index}].size`)}>
                  <option value="Beden 1">Beden 1</option>
                  <option value="Beden 2">Beden 2</option>
                  <option value="Beden 3">Beden 3</option>
                </select>
                <p>{errors.deliveryCode && "Beden seçimi gerekli!"}</p>
              </div>
              <div>
                <select
                  type="text"
                  {...register(`products.[${index}].productType`)}
                >
                  <option value="Ürün Cinsi 1">Ürün Cinsi 1</option>
                  <option value="Ürün Cinsi 2">Ürün Cinsi 2</option>
                  <option value="Ürün Cinsi 3">Ürün Cinsi 3</option>
                </select>
              </div>
              <div>
                <select
                  type="text"
                  {...register(`products.[${index}].packageType`)}
                >
                  <option value="Ambalaj 1">Ambalaj 1</option>
                  <option value="Ambalaj 2">Ambalaj 2</option>
                  <option value="Ambalaj 3">Ambalaj 3</option>
                </select>
              </div>
              <div>
                <button type="button" onClick={() => remove(index)}>
                  Remove
                </button>
              </div>
              <div>
                <Content/>
              </div>
              
            </div>
          );
        }
      )}
      
      {/* <input type="submit" /> */}
    </StyledProduct>
  );
}

export default Selections;
