import React from "react";
import { StyledCostFooter } from "./styles/CostFooter.styled";

function CostFooter({ watchFields, fields }) {
  
  
  // console.log(fields.reduce((acc,item) => acc + item.pricing.cost,0))
  // console.log(fields.map(item => (item.pricing.currencyUnit.indexOf(item.pricing.currencyUnit))))
  // const new_field=fields.map((item)=> (fields.map((loop_item)=>loop_item.pricing.currencyUnit === item.pricing.currencyUnit && item.pricing.cost + loop_item.pricing.cost)))
  const new_field=fields.map((item)=> (item.reduce((fullCost, curr) => fullCost + curr.pricing.cost )))
  console.log(new_field)
  // console.log(fields.reduce((fullTotalCost, currentValue) => fullTotalCost + fields.map(item => item.pricing.currencyUnit ===  currentValue.pricing.currencyUnit && currentValue.pricing.currencyUnit ),0))

  // console.log(fields.reduce((fullTotalCost, currentValue) => fullTotalCost + currentValue.pricing.totalCost,0))
  return (
    <StyledCostFooter>
      <div>TOPLAM TUTAR:</div>
      {/* <div>{fields.map((acc,cur) => {return acc.concat(cur.pricing.totalCost," ",cur.pricing.currencyUnit," + ")},[])}</div> */}
      
      {fields.map(
        (item, index) => 
            (
            <div key={index}>
              <div>
                {item.pricing.totalCost}
                {` ${item.pricing.currencyUnit}`}
              </div>
              <div>{fields[index + 1]?.pricing.currencyUnit && `+`}</div>
            </div>
          )
      )}
    </StyledCostFooter>
    
    
      
  );
}
export default CostFooter;
