import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import CustomDropdown from "./CustomDropdown";
import { StyledCustomerForm } from "./styles/CustomerForm.styled";
import { StyledCommonInput } from "./styles/CommonInput.styled";

function CustomerForm({ errors, register, control }) {
  const deliveryOptions = [
    { value: "Teslim Bir", label: "Teslim Bir" },
    { value: "Teslim İki", label: "Teslim İki" },
    { value: "Teslim Üç", label: "Teslim Üç" },
  ];
  const dealerOptions = [
    { value: "Satıcı Bir", label: "Satıcı Bir" },
    { value: "Satıcı İki", label: "Satıcı İki" },
    { value: "Satıcı Üç", label: "Satıcı Üç" },
  ];
  const bankOptions = [
    { value: "Banka Bir", label: "Banka Bir" },
    { value: "Banka İki", label: "Banka İki" },
    { value: "Banka Üç", label: "Banka Üç" },
  ];
  const paymentMethodOptions = [
    { value: "Nakit", label: "Nakit" },
    { value: "Kredi Kartı", label: "Kredi Kartı" },
    { value: "Cüzdan", label: "Cüzdan" },
  ];
  return (
    <StyledCustomerForm>
      <StyledCommonInput>
        <input
          type="text"
          name="receiverName"
          {...register("receiverName")}
          placeholder="Alıcı adı..."
        />
      </StyledCommonInput>

      <StyledCommonInput>
        <input
          name="currentCode"
          {...register("currentCode")}
          placeholder="Cari kod..."
        />
      </StyledCommonInput>

      <CustomDropdown
        registerName={`delivery`}
        control={control}
        options={deliveryOptions}
        placeholder={"Teslim Seçiniz..."}
        errors={errors}
      />

      <div className="inputDiv">
        <input
          name="deliveryCode"
          {...register("deliveryCode")}
          placeholder="Teslim için Özel Kod..."
        />
        <p>{errors.deliveryCode && "Teslim için Özel Kod gerekli!"}</p>
      </div>

      <CustomDropdown
        registerName={`dealer`}
        control={control}
        options={bankOptions}
        placeholder={"Satıcı Seçiniz..."}
        errors={errors}
      />

      <CustomDropdown
        registerName={`bank`}
        control={control}
        options={dealerOptions}
        placeholder={"Teslim Seçiniz..."}
        errors={errors}
      />
      <CustomDropdown
        registerName={`paymentMethod`}
        control={control}
        options={bankOptions}
        placeholder={"Teslim Seçiniz..."}
        errors={errors}
      />
      <div className="inputDiv">
        <input
          type="number"
          name="invoice"
          {...register("invoice")}
          placeholder="Fatura No..."
        />
      </div>
    </StyledCustomerForm>
  );
}

export default CustomerForm;
