import styled from "styled-components";

export const StyledContent = styled.div`

  margin: 10px;
  border: 1px solid #A5A4A7;
  border-radius: 3px;
  background-color: #D3D2D6;

  .grid2x2 {
  
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
}
.grid2x2 > div {
  display: flex; 
  flex-basis: calc(50% - 40px);  
  justify-content: center;
  flex-direction: column;
}
.grid2x2 > div > div {
  display: flex;
  justify-content: center;
  flex-direction: row;
}

  /* input  {
  box-sizing: border-box;
  width: 40% ;

  .contentFeatures {
    display: flex;
    flex-direction: row;
    box-sizing: border-box;
  }
  .a {
    display: flex;
    width: 40%;
  } */

`;
