import React, { useState } from "react";
import { useFieldArray, useForm, useWatch } from "react-hook-form";
import { StyledProduct } from "./styles/Product.styled";
import Multiselect from "multiselect-react-dropdown";
import { StyledContent } from "./styles/Content.styled";
import CustomDropdown from "./CustomDropdown";
import CustomInput from "./CustomInput";
import { ToggleSwtich } from "./styles/ToggleSwitch.styled";
import ToggleSwitch from "./ToggleSwitch";

function Content({ selectedItem, index, sub_index, errors, control }) {
  const stitchOptions = [
    { value: "Dikiş Bir", label: "Dikiş Bir" },
    { value: "Dikiş İki", label: "Dikiş İki" },
    { value: "Dikiş Üç", label: "Dikiş Üç" },
  ];

  const registerName = `products[${index}].content[${sub_index}]`;
  return (
    <StyledContent>
      <div>
        <input type="text" readOnly value={selectedItem.contentName} />
      </div>
      <div className="grid2x2">
        <div>
          <div>
            <div className="lefttext">BOY:</div>
            <CustomInput
              registerName={`${registerName}.height`}
              control={control}
              errors={errors}
              placeholder={"cm"}
              errorMessage="!"
              type={"number"}
            />
          </div>
          <div>
            <div className="lefttext">EN:</div>
            <CustomInput
              registerName={`${registerName}.width`}
              control={control}
              errors={errors}
              placeholder={"cm"}
              errorMessage="!"
              type={"number"}
            />
          </div>
          <p>jl</p>
          <ToggleSwitch/>
          <div className="toptext">
            <p>DİKİŞ</p>
            <CustomDropdown
              registerName={`${registerName}.stitchType`}
              control={control}
              options={stitchOptions}
              placeholder={"Seçiniz..."}
              errors={errors}
              errorMessage="Marka Seçiniz!"
            />
          </div>
         
        </div>
        <div>
          <div className="toptext">
            <p>ADET:</p>
            <CustomInput
              registerName={`${registerName}.width`}
              control={control}
              errors={errors}
              placeholder=""
              errorMessage="!"
              type={"number"}
              defaultValue={0}
            />
          </div>
          <div className="toptext">
            <p>Gramaj:</p>
            <CustomInput
              registerName={`${registerName}.width`}
              control={control}
              errors={errors}
              placeholder=""
              errorMessage="!"
              type={"number"}
              defaultValue={0}
            />
          </div>
        </div>
      </div>
    </StyledContent>
  );
}
export default Content;
